// page init
jQuery(function(){
	initTabs();
	initCarousel();
	initSlideShow();
	initDropDown();
	initBackgroundResize();
	initInputs();
	initSearchForm();
	initAnchors();
	initFadeHover();
	initCountDown();
});

function initCountDown(){
	jQuery('.countdown').countdown({
		dateAttrSet: 'data-until'
	});
}

function initFadeHover(){
	var animSpeed = 300,
		hoverClass = 'hover';

	jQuery('.open-close a.opener:has(>span)').each(function(){
		var holder = jQuery(this);
		var hoverBox = holder.find('>span');
		hoverBox.css({visibility:'visible',opacity:0});
		holder.on('mouseenter',function(){
			hoverBox.stop().animate({opacity:1},{duration:animSpeed});
		}).on('mouseleave',function(){
			hoverBox.stop().animate({opacity:0},{duration:animSpeed});
		})
	});

	jQuery('.button, .btn-readmore').each(function(){
		var button = jQuery(this),
			hoverBox = button.find('.hover-box');

		function hoverHandler(){
			hoverBox.stop().fadeIn();
		}

		function leaveHandler(){
			hoverBox.stop().fadeOut();
		}

		button.touchHover({
			onHover:hoverHandler,
			onLeave:leaveHandler
		});
	});

	jQuery('.main-nav li:has(.line)').each(function(){
		var link = jQuery(this),
			line = link.find('.line');

		function hoverHandler(){
			line.stop().fadeIn();
		}

		function leaveHandler(){
			line.stop().fadeOut();
		}

		link.touchHover({
			onHover:hoverHandler,
			onLeave:leaveHandler
		});
	});

	jQuery('.tabset a').animateHover({
		property:'backgroundColor',
		cleanClass:true
	});

	jQuery('.btn-more').animateHover({
		property:'backgroundPosition',
		numeric:true
	});

	jQuery('.up-button').animateHover({
		property:'backgroundColor'
	});

	jQuery('.main-nav ul li a').animateHover({
		property:'backgroundColor'
	});
}

jQuery.fn.animateHover = function(options){
	var options = jQuery.extend({
		hoverClass:'hover',
		animSpeed:400,
		property:null,
		numeric:false,
		cleanClass:false
	}, options);

	return this.each(function(){
		if (!options.property) return;

		var elem = jQuery(this),
			styles, classes;

		if (options.cleanClass){
			classes = elem.attr('class');
			elem.removeAttr('class');
		}

		styles = getStyle(elem, options.property);
		
		if (options.cleanClass){	
			elem.attr('class', classes);
		}

		function hoverHandler(){
			var tmpObj = {};
			tmpObj[options.property] = options.numeric ? parseInt(styles.hover) : styles.hover;
			console.log(tmpObj)
			elem.removeClass(options.hoverClass);

			elem.stop().animate(tmpObj, options.animSpeed, function(){
				elem.addClass(options.hoverClass);
			});
		}

		function leaveHandler(){
			var tmpObj = {};
			tmpObj[options.property] = options.numeric ? parseInt(styles.init) : styles.init;

			elem.stop().animate(tmpObj, options.animSpeed);
		}

		elem.touchHover({
			onHover:hoverHandler,
			onLeave:leaveHandler
		});
	});

	function getStyle(elem, style){
		elem.removeClass(options.hoverClass);
		var init = elem.css(style);
		elem.addClass(options.hoverClass);
		var hover = elem.css(style);
		elem.removeClass(options.hoverClass);
		
		return {
			init:init,
			hover:hover
		};
	}
}

function initAnchors(){
	var animSpeed = 300;
	var page = jQuery('html,body');
	jQuery('a.up-button[href^="#"]').each(function(){
		var link = jQuery(this);
		var section = jQuery(link.attr('href'));
		if(section.length){
			link.on('click',function(e){
				e.preventDefault();
				page.stop().animate({scrollTop: section.offset().top},{duration:animSpeed});
			})
		}
	})
}

function initSearchForm(){
	var activeClass = 'opened';
	var animSpeed = 500;
	jQuery('.open-close:has(.search-holder)').each(function(){
		var holder = jQuery(this);
		var slider = holder.find('.search-holder');
		var contentBox = holder.find('form.search-form');
		var inputSearch = holder.find('input:text');
		function showForm(){
			holder.addClass(activeClass);
			slider.stop().animate({width: contentBox.outerWidth(true)},{duration:animSpeed});
		}
		function hideForm(){
			slider.stop().animate({width: 0},{duration:animSpeed,easing:'linear',complete:function(){
				holder.removeClass(activeClass);
			}});
		}
		holder.find('.opener').on('click',function(e){
			e.preventDefault();
			if(!holder.hasClass(activeClass)){
				showForm();
				inputSearch.focus();
			} else {
				hideForm();
			}
		})
		holder.find('.close').on('click',function(e){
			e.preventDefault();
			hideForm();
		})
	})
}

// scroll gallery init
function initCarousel() {
	jQuery('div.carousel').scrollGallery({
		mask: 'div.mask',
		slider: 'div.slideset',
		slides: 'div.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		generatePagination: '.pagination',
		stretchSlideToMask: true,
		autoRotation: false,
		switchTime: 3000,
		animSpeed: 500
	});
}

// fade gallery init
function initSlideShow() {
	jQuery('div.slideshow').fadeGallery({
		slides: 'div.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		generatePagination: '.pagination',
		event: 'click',
		autoRotation: false,
		switchTime: 3000,
		animSpeed: 500
	});
}

// content tabs init
function initTabs() {
	jQuery('ul.tabset').contentTabs({
		animSpeed: 400,
		effect: 'fade',
		tabLinks: 'a'
	});
}

// animated navigation init
function initDropDown() {
	jQuery('ul.main-nav').animDropdown({
		items: 'li',
		drop: '>ul',
		animSpeed: 400,
		effect: 'slide'
	});
}

// stretch background to fill blocks
function initBackgroundResize() {
	jQuery('.bg-stretch').each(function() {
		ImageStretcher.add({
			container: this,
			image: '>img'
		});
	});
}

// clear inputs on focus
function initInputs() {
	PlaceholderInput.replaceByOptions({
		// filter options
		clearInputs: true,
		clearTextareas: false,
		clearPasswords: false,
		skipClass: 'default',
		
		// input options
		wrapWithElement: false,
		showUntilTyping: false,
		getParentByClass: false,
		placeholderAttr: 'value'
	});
}

