			this.holder = $(this.options.holder);
			this.untilDate = this.holder.attr(this.options.dateAttrSet) ? new Date(this.holder.attr(this.options.dateAttrSet)) : false;
			this.timer;
			this.daysHolder = this.holder.find(this.options.daysHolderSelector);
			this.hoursHolder = this.holder.find(this.options.hoursHolderSelector);
			this.minutesHolder = this.holder.find(this.options.minutesHolderSelector);
			this.secondsHolder = this.holder.find(this.options.secondsHolderSelector);
			this.now = new Date();
			this.constantDayTime = 1000 * 60 * 60 * 24;
			this.constantHourTime = 1000 * 60 * 60;
			this.constantMinuteTime = 1000 * 60;
